import React, { useState } from 'react';
import {
  CalciteShell ,CalciteShellPanel, CalciteActionBar, 
  CalcitePickList, CalciteShellCenterRow, CalcitePopoverManager, 
  CalciteAction, CalcitePopover, CalcitePanel, CalcitePickListItem 
} from '@esri/calcite-components-react';
import '@esri/calcite-components/dist/calcite/calcite.css';
import './App.css';

function App() {
  const [sliderValue, setSliderValue] = useState(70);
  const InputURL="http://sampleserver6.arcgisonline.com/arcgis/rest/services";    
  const URLForServices = InputURL + '?f=pjson';   
  const ServiceName = "ImageServer";                    
      $.getJSON(URLForServices, function(data) { 
        const ServiceList = data.services.filter( s => s.type === ServiceName).map( s => s.name);
        ServiceList.forEach( function(item, index) {
        $("calcite-pick-list").append(
          "<calcite-pick-list-item label='"+item+"'value='"+item+"label' onclick='ServiceDescription(\""+item+"\")'></calcite-pick-list-item>"               
        )});
      });
      function ServiceDescription(ServiceListItem) {
        const EndpointURL = InputURL+'/'+ServiceListItem+'/'+ServiceName+'?f=pjson'; 
        $.getJSON(EndpointURL, function(data) {
        const TextServiceName = `<b>NAME: </b> ${data.name} <br>`
        const TextServiceDescription = `<b>DESCRIPTION: </b> ${data.description}`
        $(".NamePanel").html(TextServiceName);
        $(".DescriptionPanel").html(TextServiceDescription);
      })};                
  return (
    /*
      <h1>Hello, React</h1>
      <CalciteButton>Test</CalciteButton>
      <CalciteIcon icon="banana" />
      <CalciteSlider
        min="1"
        max="100"
        value={sliderValue}
        step="1"
        onCalciteSliderUpdate={(e) => setSliderValue(e.target.value)} />
      <p>The slider currently has a value of {sliderValue}</p>
     */

    <CalciteShell dir="ltr" class="calcite-theme-light">
      <center><h2>ESRI Assignment 1 - Ameet Rahane</h2></center>
      <CalciteShellPanel slot="primary-panel" position="start" detached-height-scale="l" width-scale="m">   
        <CalciteActionBar class="calcite-theme-dark" slot="action-bar"></CalciteActionBar>
          <CalcitePickList>
            <CalcitePickListItem label="T. Rex" description="Arm strength impaired" value="trex">
            <CalciteAction slot="actions-end" icon="circle"></CalciteAction>
            </CalcitePickListItem>
            </CalcitePickList> 
      </CalciteShellPanel>  
      <CalciteShellCenterRow height-scale="s" position="end" slot="center-row">   

      </CalciteShellCenterRow>
      <CalciteShellPanel slot="contextual-panel" position="end" detached-height-scale="l" width-scale="m"/>
        <CalciteActionBar class="calcite-theme-light" slot="action-bar">
          <CalcitePopoverManager>
            <CalciteAction text="Idea" id="reference-element" label="Add Item" icon="lightbulb" appearance="solid" scale="m"></CalciteAction>                    
          </CalcitePopoverManager>
          <CalcitePopover class="calcite-theme-light" reference-element="reference-element" placement="auto" offset-distance="6" offset-skidding="0" open="" text-close="Close">
            <div>
              <p>This application was built by Esri's <a href="mailto: arahane@esri.com">@arahane</a> using 
              <a href="https://esri.github.io/calcite-components/">Calcite Components</a></p>      
            </div>
          </CalcitePopover>       
        </CalciteActionBar>
        <CalcitePanel> 
        </CalcitePanel>
    </CalciteShell>

  );
}

export default App;
