  return (
     <div class = "App">
   <calcite-shell dir="ltr" class="calcite-theme-light">
  <header slot="header">
    <h2>My Shell Header</h2>
  </header>
  <calcite-shell-panel slot="primary-panel" position="start">
    <calcite-action-bar class="calcite-theme-dark" slot="action-bar">
      <calcite-action-group>
        <calcite-action
          text="Add"
          label="Add Item"
          icon="plus"
        ></calcite-action>
        <calcite-action
          text="Save"
          label="Save Item"
          icon="save"
        ></calcite-action>
      </calcite-action-group>
      <calcite-action-group>
        <calcite-action
          text="Layers"
          label="View Layers"
          icon="layers"
        ></calcite-action>
      </calcite-action-group>
    </calcite-action-bar>
    <p>My Leading Panel</p>
  </calcite-shell-panel>
  <div></div>
  <calcite-shell-center-row height-scale="s" position="end" slot="center-row">
    <div>
      <span>My Shell Center Row</span>
    </div>
  </calcite-shell-center-row>
  <calcite-shell-panel slot="contextual-panel" position="end">
    <calcite-action-bar class="calcite-theme-light" slot="action-bar">
      <calcite-action-group>
        <calcite-action
          text="Idea"
          label="Add Item"
          icon="lightbulb"
        ></calcite-action>
        <calcite-action
          text="Information"
          label="Save Item"
          icon="information"
        ></calcite-action>
      </calcite-action-group>
      <calcite-action-group>
        <calcite-action
          text="Question"
          label="View Layers"
          icon="question"
        ></calcite-action>
      </calcite-action-group>
    </calcite-action-bar>
    <p>My Trailing Panel</p>
  </calcite-shell-panel>
  <footer slot="footer">My Shell Footer</footer>
</calcite-shell>
</div>



